mapper.select:
	SELECT ID, UID, CONTENT, CREATED_AT, UPDATED_AT
	FROM POST_COMMENT
    WHERE DELETED = 0

	ORDER BY ID DESC
    LIMIT #{size}
	OFFSET #{offset}

SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END
FROM POST_COMMENT
WHERE ID = #{id} AND UID = #{uid}

mapper.update:
UPDATE POST
SET 
CONTENT = #{content}
WHERE ID = #{id}

UPDATE POST_COMMENT
SET DELETED = 1
WHERE ID = #{id}
